import React, { useState, useEffect } from 'react';

const GroupsListPopover = ({ data, onClose, onAction }) => {
  const [groups, setGroups] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchGroups();
  }, []);

  const fetchGroups = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/groups');
      const data = await response.json();
      setGroups(data.groups || []);
    } catch (error) {
      console.error('Error fetching groups:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleGroupSelect = (group) => {
    setSelectedGroup(group);
  };

  const handleJoinGroup = () => {
    if (selectedGroup) {
      onAction('join-group', selectedGroup);
      onClose();
    }
  };

  if (loading) {
    return (
      <div className="popover-header">
        <h3>Groups List</h3>
        <div className="loading">Loading groups...</div>
      </div>
    );
  }

  return (
    <div className="popover-container">
      <div className="popover-header">
        <h3>Groups List</h3>
        <button className="close-button" onClick={onClose}>Ã—</button>
      </div>

      <div className="popover-body">
        {groups.length === 0 ? (
          <div className="empty-state">
            <p>No groups available</p>
            <p>Create a new group to get started</p>
          </div>
        ) : (
          <div className="groups-list">
            {groups.map((group) => (
              <div
                key={group._id}
                className={`group-item ${selectedGroup?._id === group._id ? 'selected' : ''}`}
                onClick={() => handleGroupSelect(group)}
              >
                <input
                  type="radio"
                  checked={selectedGroup?._id === group._id}
                  onChange={() => handleGroupSelect(group)}
                  name="group-selection"
                />
                <div className="group-info">
                  <span className="group-name">{group.name}</span>
                  <span className="group-members">{group.members?.length || 0} members</span>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="popover-footer">
        <button className="cancel-button" onClick={onClose}>Cancel</button>
        <button 
          className="action-button" 
          onClick={handleJoinGroup}
          disabled={!selectedGroup}
        >
          Join Group
        </button>
      </div>
    </div>
  );
};

export default GroupsListPopover;
