import React, { useState, useEffect } from 'react';

const GroupsListPopover = ({ data, onClose, onAction, currentUserEmail }) => {
  const [userGroups, setUserGroups] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchUserGroups();
  }, []);

  const fetchUserGroups = async () => {
    try {
      const response = await fetch(`http://localhost:3001/api/groups/user/${currentUserEmail}`);
      const data = await response.json();
      setUserGroups(data.groups || []);
    } catch (error) {
      console.error('Error fetching user groups:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAcceptGroup = async (groupId) => {
    try {
      const response = await fetch(`http://localhost:3001/api/groups/${groupId}/accept`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ userEmail: currentUserEmail })
      });

      if (response.ok) {
        fetchUserGroups(); // Refresh the list
      }
    } catch (error) {
      console.error('Error accepting group:', error);
    }
  };

  const handleDeclineGroup = async (groupId) => {
    try {
      const response = await fetch(`http://localhost:3001/api/groups/${groupId}/decline`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ userEmail: currentUserEmail })
      });

      if (response.ok) {
        fetchUserGroups(); // Refresh the list
      }
    } catch (error) {
      console.error('Error declining group:', error);
    }
  };

  const handleRemoveMember = async (groupId, memberEmail) => {
    try {
      const response = await fetch(`http://localhost:3001/api/groups/${groupId}/members/${memberEmail}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ creatorEmail: currentUserEmail })
      });

      if (response.ok) {
        fetchUserGroups(); // Refresh the list
      }
    } catch (error) {
      console.error('Error removing member:', error);
    }
  };

  const handleDeleteGroup = async (groupId) => {
    if (!window.confirm('Are you sure you want to delete this group?')) {
      return;
    }

    try {
      const response = await fetch(`http://localhost:3001/api/groups/${groupId}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ creatorEmail: currentUserEmail })
      });

      if (response.ok) {
        fetchUserGroups(); // Refresh the list
      }
    } catch (error) {
      console.error('Error deleting group:', error);
    }
  };

  const getMemberStatus = (group, memberEmail) => {
    const member = group.members.find(m => m.email === memberEmail);
    return member ? member.status : 'unknown';
  };

  const isGroupCreator = (group) => {
    return group.createdBy === currentUserEmail;
  };

  const isPendingMember = (group) => {
    return getMemberStatus(group, currentUserEmail) === 'pending';
  };

  if (loading) {
    return (
      <div className="popover-header">
        <h3>My Groups</h3>
        <div className="loading">Loading groups...</div>
      </div>
    );
  }

  return (
    <div className="popover-container">
      <div className="popover-header">
        <h3>My Groups</h3>
        <button className="close-button" onClick={onClose}>×</button>
      </div>

      <div className="popover-body">
        {userGroups.length === 0 ? (
          <div className="empty-state">
            <p>No groups available</p>
            <p>Create a new group to get started</p>
          </div>
        ) : (
          <div className="groups-list">
            {userGroups.map((group) => (
              <div key={group._id} className="group-item">
                <div className="group-header">
                  <div className="group-info">
                    <span className="group-name">{group.name}</span>
                    <span className="group-members">
                      {group.members.length} members
                    </span>
                  </div>
                  <div className="group-actions">
                    {isPendingMember(group) ? (
                      <>
                        <button 
                          className="accept-button"
                          onClick={() => handleAcceptGroup(group._id)}
                        >
                          Accept
                        </button>
                        <button 
                          className="decline-button"
                          onClick={() => handleDeclineGroup(group._id)}
                        >
                          Decline
                        </button>
                      </>
                    ) : (
                      <button 
                        className="start-call-button"
                        onClick={() => onAction('start-group-call', group)}
                      >
                        Start Call
                      </button>
                    )}
                  </div>
                </div>

                <div className="group-members-list">
                  {group.members.map((member) => (
                    <div key={member.email} className="member-item">
                      <div className="member-info">
                        <span className="member-email">{member.email}</span>
                        <span className={`member-status ${member.status}`}>
                          {member.status === 'pending' ? '⏳ Pending' : '✅ Accepted'}
                        </span>
                      </div>
                      {isGroupCreator(group) && member.email !== currentUserEmail && (
                        <button 
                          className="remove-member-button"
                          onClick={() => handleRemoveMember(group._id, member.email)}
                          title="Remove member"
                        >
                          ✕
                        </button>
                      )}
                    </div>
                  ))}
                </div>

                {isGroupCreator(group) && (
                  <div className="group-creator-actions">
                    <button 
                      className="delete-group-button"
                      onClick={() => handleDeleteGroup(group._id)}
                    >
                      Delete Group
                    </button>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="popover-footer">
        <button className="cancel-button" onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default GroupsListPopover;
