/* eslint-disable default-case */
import React, { useEffect, useRef, useState } from 'react';
import { io } from 'socket.io-client';
import './GroupCall.css';

const GroupCall = ({ user, onLeave }) => {
    const [localStream, setLocalStream] = useState(null);
    const [remoteStreams, setRemoteStreams] = useState(new Map());
    const [isConnected, setIsConnected] = useState(false);
    const [participants, setParticipants] = useState([]);
    const [isMuted, setIsMuted] = useState(false);
    const [isVideoOff, setIsVideoOff] = useState(false);
    const [activeRoom, setActiveRoom] = useState(null);
    const [availableRooms, setAvailableRooms] = useState([]);

    const socketRef = useRef(null);
    const localVideoRef = useRef(null);
    const peerConnections = useRef(new Map());

    useEffect(() => {
        // Initialize Socket.IO connection
        socketRef.current = io('http://localhost:3001');

        // Connection event handlers
        socketRef.current.on('connect', () => {
            console.log('Connected to call server');
            setIsConnected(true);
        });

        socketRef.current.on('disconnect', () => {
            console.log('Disconnected from call server');
            setIsConnected(false);
            cleanup();
        });

        // Room events
        socketRef.current.on('room:joined', (roomData) => {
            console.log('Joined room:', roomData);
            setActiveRoom(roomData.roomId);
            setParticipants(roomData.participants);
        });

        socketRef.current.on('participant:joined', (participant) => {
            console.log('Participant joined:', participant);
            setParticipants(prev => [...prev, participant]);
        });

        socketRef.current.on('participant:left', (participant) => {
            console.log('Participant left:', participant);
            setParticipants(prev => prev.filter(p => p.id !== participant.id));
            handleParticipantDisconnect(participant.id);
        });

        // WebRTC signaling events
        socketRef.current.on('webrtc:offer', async ({ from, offer }) => {
            console.log('Received offer from:', from);
            await handleOffer(from, offer);
        });

        socketRef.current.on('webrtc:answer', async ({ from, answer }) => {
            console.log('Received answer from:', from);
            await handleAnswer(from, answer);
        });

        socketRef.current.on('webrtc:ice-candidate', async ({ from, candidate }) => {
            console.log('Received ICE candidate from:', from);
            await handleIceCandidate(from, candidate);
        });

        // Initialize local media stream
        initializeLocalStream();

        return () => {
            cleanup();
        };
    }, []);

    const initializeLocalStream = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({
                audio: true,
                video: true
            });
            setLocalStream(stream);
            if (localVideoRef.current) {
                localVideoRef.current.srcObject = stream;
            }
        } catch (error) {
            console.error('Error accessing media devices:', error);
        }
    };

    const handleOffer = async (from, offer) => {
        try {
            const peerConnection = createPeerConnection(from);
            await peerConnection.setRemoteDescription(new RTCSessionDescription(offer));
            const answer = await peerConnection.createAnswer();
            await peerConnection.setLocalDescription(answer);

            socketRef.current.emit('webrtc:answer', {
                to: from,
                answer
            });
        } catch (error) {
            console.error('Error handling offer:', error);
        }
    };

    const handleAnswer = async (from, answer) => {
        try {
            const peerConnection = peerConnections.current.get(from);
            if (peerConnection) {
                await peerConnection.setRemoteDescription(new RTCSessionDescription(answer));
            }
        } catch (error) {
            console.error('Error handling answer:', error);
        }
    };

    const handleIceCandidate = async (from, candidate) => {
        try {
            const peerConnection = peerConnections.current.get(from);
            if (peerConnection) {
                await peerConnection.addIceCandidate(new RTCIceCandidate(candidate));
            }
        } catch (error) {
            console.error('Error handling ICE candidate:', error);
        }
    };

    const createPeerConnection = (participantId) => {
        const configuration = {
            iceServers: [
                { urls: 'stun:stun.l.google.com:19302' },
                { urls: 'stun:stun1.l.google.com:19302' }
            ]
        };

        const peerConnection = new RTCPeerConnection(configuration);

        // Add local stream
        if (localStream) {
            localStream.getTracks().forEach(track => {
                peerConnection.addTrack(track, localStream);
            });
        }

        // Handle ICE candidates
        peerConnection.onicecandidate = (event) => {
            if (event.candidate) {
                socketRef.current.emit('webrtc:ice-candidate', {
                    to: participantId,
                    candidate: event.candidate
                });
            }
        };

        // Handle remote stream
        peerConnection.ontrack = (event) => {
            const [stream] = event.streams;
            setRemoteStreams(prev => new Map(prev).set(participantId, stream));
        };

        peerConnections.current.set(participantId, peerConnection);
        return peerConnection;
    };

    const handleParticipantDisconnect = (participantId) => {
        // Clean up peer connection
        const peerConnection = peerConnections.current.get(participantId);
        if (peerConnection) {
            peerConnection.close();
            peerConnections.current.delete(participantId);
        }

        // Remove remote stream
        setRemoteStreams(prev => {
            const newStreams = new Map(prev);
            newStreams.delete(participantId);
            return newStreams;
        });
    };

    const cleanup = () => {
        // Stop local stream
        if (localStream) {
            localStream.getTracks().forEach(track => track.stop());
        }

        // Close all peer connections
        peerConnections.current.forEach(connection => {
            connection.close();
        });
        peerConnections.current.clear();

        // Clear remote streams
        setRemoteStreams(new Map());

        // Disconnect socket
        if (socketRef.current) {
            socketRef.current.disconnect();
        }
    };

    const toggleAudio = () => {
        if (localStream) {
            const audioTrack = localStream.getAudioTracks()[0];
            if (audioTrack) {
                audioTrack.enabled = !audioTrack.enabled;
                setIsMuted(!audioTrack.enabled);
            }
        }
    };

    const toggleVideo = () => {
        if (localStream) {
            const videoTrack = localStream.getVideoTracks()[0];
            if (videoTrack) {
                videoTrack.enabled = !videoTrack.enabled;
                setIsVideoOff(!videoTrack.enabled);
            }
        }
    };

    const leaveRoom = () => {
        cleanup();
        onLeave();
    };

    return (
        <div className="group-call-container">
            <div className="video-grid">
                {/* Local video */}
                <div className="video-container local-video">
                    <video
                        ref={localVideoRef}
                        autoPlay
                        playsInline
                        muted
                    />
                    <div className="video-overlay">
                        <span>{user.name} (You)</span>
                    </div>
                </div>

                {/* Remote videos */}
                {Array.from(remoteStreams).map(([participantId, stream]) => (
                    <div key={participantId} className="video-container">
                        <video
                            autoPlay
                            playsInline
                            ref={el => {
                                if (el) el.srcObject = stream;
                            }}
                        />
                        <div className="video-overlay">
                            <span>{participants.find(p => p.id === participantId)?.name || 'Participant'}</span>
                        </div>
                    </div>
                ))}
            </div>

            <div className="controls">
                <button onClick={toggleAudio}>
                    {isMuted ? 'üîá Unmute' : 'üîä Mute'}
                </button>
                <button onClick={toggleVideo}>
                    {isVideoOff ? 'üì∑ Start Video' : 'üé• Stop Video'}
                </button>
                <button onClick={leaveRoom} className="leave-btn">
                    ‚ùå Leave Call
                </button>
            </div>
        </div>
    );
};

export default GroupCall;
