import React, { useState, useEffect, useRef, useCallback } from 'react';
import './App.css';
import GroupCall from './components/GroupCall';

function App() {
  const [email, setEmail] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [showUserList, setShowUserList] = useState(false);
  const [activeRoom, setActiveRoom] = useState(null);

  // Function to fetch online users
  const fetchOnlineUsers = useCallback(async () => {
    try {
      const response = await fetch('http://localhost:3001/users');
      const data = await response.json();
      // Filter out current user
      setOnlineUsers(data.users.filter(user => user !== email));
    } catch (error) {
      console.error('Error fetching online users:', error);
    }
  }, [email]);

  // Fetch online users periodically when logged in
  useEffect(() => {
    if (isLoggedIn) {
      fetchOnlineUsers(); // Initial fetch
      const interval = setInterval(fetchOnlineUsers, 5000); // Refresh every 5 seconds
      return () => clearInterval(interval);
    }
  }, [isLoggedIn, fetchOnlineUsers]);

  const handleLogin = (e) => {
    e.preventDefault();
    if (email && email.includes('@')) {  // Basic email validation
      setIsLoggedIn(true);
    } else {
      alert('Please enter a valid email address');
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setEmail('');
    setShowUserList(false);
    setActiveRoom(null);
  };

  const getProfileInitial = (userEmail) => {
    return userEmail.charAt(0).toUpperCase();
  };

  const handleLeave = () => {
    setActiveRoom(null);
  };

  return (
    <div className="App">
      {!isLoggedIn ? (
        <div className="login-container">
          <div className="welcome-header">
            <h1>Welcome to RealTime Connect</h1>
            <p>Experience seamless video calling with your team</p>
          </div>
          <div className="login-box">
            <h2>Login</h2>
            <form onSubmit={handleLogin}>
              <input
                type="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <button type="submit">Login</button>
            </form>
          </div>
        </div>
      ) : !activeRoom ? (
        <div className="main-container">
          <div className="header">
            <div className="user-info">
              <div className="avatar">{getProfileInitial(email)}</div>
              <span>{email}</span>
            </div>
            <button onClick={handleLogout} className="logout-btn">Logout</button>
          </div>

          <div className="dashboard">
            <div className="welcome-message">
              <h2>Welcome, {email.split('@')[0]}!</h2>
              <p>Start a video call with your team members</p>
            </div>

            <div className="user-list">
              <h3>Online Users</h3>
              <div className="user-grid">
                {onlineUsers.map((user) => (
                  <div key={user} className="user-card">
                    <div className="user-avatar">{getProfileInitial(user)}</div>
                    <span className="user-email">{user}</span>
                    <div className="user-status">
                      <span className="status-dot"></span>
                      Online
                    </div>
                    <button 
                      onClick={() => setActiveRoom(user)}
                      className="call-btn"
                    >
                      Start Group Call
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      ) : (
        <GroupCall
          user={{ id: email, name: email }}
          onLeave={handleLeave}
          initialParticipants={[email, activeRoom]}
        />
      )}
    </div>
  );
}

export default App;
