import React, { useState, useEffect, useRef, useCallback } from 'react';
import './App.css';
import GroupCall from './components/GroupCall';

function App() {
  const [email, setEmail] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [showUserList, setShowUserList] = useState(false);
  const [activeRoom, setActiveRoom] = useState(null);
  const wsRef = useRef(null);

  // WebSocket message handler
  const handleWebSocketMessage = useCallback((event) => {
    const data = JSON.parse(event.data);
    if (data.type === 'user-list') {
      setOnlineUsers(data.users.filter(user => user !== email));
    }
  }, [email]);

  // Initialize WebSocket connection and fetch initial user list
  useEffect(() => {
    if (isLoggedIn && !wsRef.current) {
      // Initial fetch of online users
      fetch('http://localhost:3001/api/users')
        .then(response => response.json())
        .then(data => {
          setOnlineUsers(data.users.filter(user => user !== email));
        })
        .catch(error => {
          console.error('Error fetching initial user list:', error);
        });

      // Setup WebSocket connection
      wsRef.current = new WebSocket('ws://localhost:3001');

      wsRef.current.onopen = () => {
        console.log('Connected to server');
        // Register user when connection is established
        wsRef.current.send(JSON.stringify({
          type: 'register',
          email: email
        }));
      };

      wsRef.current.onmessage = handleWebSocketMessage;

      wsRef.current.onclose = () => {
        console.log('Disconnected from server');
        wsRef.current = null;
      };

      wsRef.current.onerror = (error) => {
        console.error('WebSocket error:', error);
      };
    }

    return () => {
      if (wsRef.current) {
        wsRef.current.close();
        wsRef.current = null;
      }
    };
  }, [isLoggedIn, email, handleWebSocketMessage]);

  const handleLogin = (e) => {
    e.preventDefault();
    if (email && email.includes('@')) {  // Basic email validation
      setIsLoggedIn(true);
    } else {
      alert('Please enter a valid email address');
    }
  };

  const handleLogout = () => {
    if (wsRef.current) {
      wsRef.current.close();
      wsRef.current = null;
    }
    setIsLoggedIn(false);
    setEmail('');
    setShowUserList(false);
    setActiveRoom(null);
    setOnlineUsers([]);
  };

  const getProfileInitial = (userEmail) => {
    return userEmail.charAt(0).toUpperCase();
  };

  const handleLeave = () => {
    setActiveRoom(null);
  };

  return (
    <div className="App">
      {!isLoggedIn ? (
        <div className="landing-container">
          <div className="landing-content">
            <div className="welcome-section">
              <h1>Welcome to RealTime Connect</h1>
              <p className="welcome-description">
                Experience seamless video calling with your team members. Connect instantly,
                collaborate effectively, and stay productive from anywhere.
              </p>
              <div className="feature-list">
                <div className="feature-item">
                  <span className="feature-icon">ðŸŽ¥</span>
                  <span>HD Video Quality</span>
                </div>
                <div className="feature-item">
                  <span className="feature-icon">ðŸ”’</span>
                  <span>Secure Connection</span>
                </div>
                <div className="feature-item">
                  <span className="feature-icon">ðŸ‘¥</span>
                  <span>Group Calling</span>
                </div>
              </div>
            </div>
            <div className="login-section">
              <div className="login-box">
                <h2>Get Started</h2>
                <p className="login-description">Enter your email to join the conversation</p>
                <form onSubmit={handleLogin}>
                  <div className="input-group">
                    <input
                      type="email"
                      placeholder="Enter your email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      required
                    />
                  </div>
                  <button type="submit" className="login-button">
                    Join Now
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      ) : !activeRoom ? (
        <div className="main-container">
          <div className="header">
            <div className="user-info">
              <div className="avatar">{getProfileInitial(email)}</div>
              <span className="user-email">{email}</span>
            </div>
            <button onClick={handleLogout} className="logout-btn">Logout</button>
          </div>

          <div className="dashboard">
            <div className="welcome-message">
              <h2>Welcome, {email.split('@')[0]}!</h2>
              <p>Start a video call with your team members</p>
            </div>

            <div className="members-section">
              <h3>Online Members</h3>
              <div className="members-grid">
                {onlineUsers.map((user) => (
                  <div key={user} className="member-card">
                    <div className="member-avatar">{getProfileInitial(user)}</div>
                    <div className="member-info">
                      <span className="member-name">{user.split('@')[0]}</span>
                      <span className="member-email">{user}</span>
                      <div className="member-status">
                        <span className="status-dot"></span>
                        <span>Online</span>
                      </div>
                    </div>
                    <button 
                      onClick={() => setActiveRoom(user)}
                      className="call-button"
                    >
                      <span className="call-icon">ðŸ“ž</span>
                      Call
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      ) : (
        <GroupCall
          user={{ id: email, name: email }}
          onLeave={handleLeave}
          initialParticipants={[email, activeRoom]}
        />
      )}
    </div>
  );
}

export default App;
